update.foreach:
update order_detail


        end_time = #{orderDetail.endTime},


        start_time = #{orderDetail.startTime}


where id = #{orderDetail.id}

mapper.select:
select od.name, sum(od.number) as number from order_detail od, orders o


        and o.order_time >= #{start}


        and o.order_time <= #{end}


        and o.status = #{status}

    od.order_id = o.id

group by od.name
order by number desc
limit 10
