mapper.select:
select * from orders


        and user_id = #{userId}


        and status = #{status}


        and number = #{number}


        and phone like concat('%',#{phone},'%')


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select * from orders


        and user_id = #{userId}


        and status = #{status}


        and number = #{number}


        and phone like concat('%',#{phone},'%')


        and order_time = #{orderTime}

select sum(amount) from orders


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and status = #{status}

select count(*) from orders


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


        and status = #{status}
