mapper.select:
select * from pre_order

    user_id = #{userId}
    and package_id = #{packageId}
    and service_id = #{serviceId}
    and service_detail = #{serviceDetail}

mapper.update:
update pre_order
number = #{number}, create_time = #{createTime}, user_id = #{userId}, package_id = #{packageId}, service_id = #{serviceId}, service_detail = #{serviceDetail}, amount = #{amount},
where id = #{id}

mapper.delete:
delete from pre_order

    user_id = #{userId}
    and package_id = #{packageId}
    and service_id = #{serviceId}
    and service_detail = #{serviceDetail}
