mapper.update:
update service
category_id = #{categoryId}, name = #{name}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select service.*,category.name as category_name  from service
left join category on service.category_id=category.id

    service.category_id = #{categoryId}
    and service.name like concat('%', #{name}, '%')
    and service.status = #{status}

order by id desc

select * from service

    category_id = #{categoryId}
    and name like concat('%', #{name}, '%')
    and status = #{status}

select count(id) from service


        and status = #{status}


        and category_id = #{categoryId}
